using Northwind_BackEndSqlEntities.Entities;
using Northwind_BackEndCommon.Services;
using Northwind_Common.IndirectReferenceTransformerModels;
namespace Northwind_BackEndCommon.IndirectReferenceTransformers;
public partial class IRTransformers : IIRTransformers
{
	public Northwind_dbo_Categories_IR ToIndirectModel(Northwind_dbo_Categories input, Boolean isSecondaryLookup = false)
	{
		var retData = new Northwind_dbo_Categories_IR(
			categoryID_IR_ : _encryptionDecryptionService!.EncInt32(input.CategoryID),
			categoryName_ : input.CategoryName,
			description_ : input.Description,
			picture_ : input.Picture
			);
		if (!isSecondaryLookup && input.FK_Products_Categories_RefBy!.Any()) retData.FK_Products_Categories_RefBy_IR = input.FK_Products_Categories_RefBy!.Select(x => ToIndirectModel(x, true)).ToList()!;
			retData.PrimaryKeyHashedForUniqueObjectComparison = _encryptionDecryptionService!.CreateHash(retData.CategoryID_IR);
		return retData;
	}
	public Northwind_dbo_Categories ToEntity(Northwind_dbo_Categories_IR input, Boolean isSecondaryLookup = false)
	{
		var retData = new Northwind_dbo_Categories(		
			categoryID_ : _encryptionDecryptionService.DecInt32(input.CategoryID_IR),
			categoryName_ : input.CategoryName ?? String.Empty,
			description_ : input.Description,
			picture_ : input.Picture
			);
		return retData;
	}
}
