using Microsoft.Extensions.Logging;
using Microsoft.EntityFrameworkCore;
using Z.EntityFramework.Plus;
using Northwind_BackEndDatabaseClient;
using Northwind_BackEndSqlEntities.Entities;
namespace Northwind_BackEndDatabaseClient.Repositories;
public class Northwind_dbo_Shippers_Repository : GenericTableRepository<Northwind_dbo_Shippers>, INorthwind_dbo_Shippers_Repository
{
    private readonly ILogger<Northwind_dbo_Shippers_Repository> _logger;    
    public Northwind_dbo_Shippers_Repository
    (
        ILogger<Northwind_dbo_Shippers_Repository> logger
        ,Northwind_Context dbContext
    ) : base(dbContext)
    {
        _logger = logger;
    }
	public async Task<IEnumerable<Northwind_dbo_Shippers>?> GetByShipperID(Int32 shipperID_)
	{
		return await _dbContext.Northwind_dbo_Shippers!
			.Where(x => x.ShipperID == shipperID_)
			.AsNoTracking()
			.ToListAsync();
	}
	public async Task UpdateByShipperID(Int32 shipperID_, Northwind_dbo_Shippers entity)
	{
		await _dbContext.Northwind_dbo_Shippers!
			.Where(x => x.ShipperID == shipperID_)
			.UpdateFromQueryAsync(x => new Northwind_dbo_Shippers(){ CompanyName = entity.CompanyName, Phone = entity.Phone });
	}
	public async Task DeleteByShipperID(Int32 shipperID_)
	{
		await _dbContext.Northwind_dbo_Shippers!
			.Where(x => x.ShipperID == shipperID_)
			.DeleteFromQueryAsync();
	}
}
